// Code ultra-robuste ‚Äì force une alerte pour test
let cpu = 0;
let load = 0;

// R√©cup√©ration s√©curis√©e des donn√©es CPU (premier item)
if (items[0] && items[0].json && items[0].json.data && items[0].json.data.length > 0) {
  const cpuArray = items[0].json.data[0];
  if (Array.isArray(cpuArray) && cpuArray.length > 1) {
    cpu = parseFloat(cpuArray[1]) || 0;
  }
}

// R√©cup√©ration s√©curis√©e des donn√©es Load (deuxi√®me item)
if (items[1] && items[1].json && items[1].json.data && items[1].json.data.length > 0) {
  const loadArray = items[1].json.data[0];
  if (Array.isArray(loadArray) && loadArray.length > 1) {
    load = parseFloat(loadArray[1]) || 0;
  }
}

// Arrondi
cpu = Number(cpu.toFixed(2));
load = Number(load.toFixed(2));

// === FORCER L'ALERTE POUR TEST ===
const alert = true;  // ‚Üê Ici on force true pour d√©clencher l'alerte √† coup s√ªr

// Message d'alerte (personnalisable)
const message = `üö® TEST ALERTE FORC√âE üö®
üíª CPU r√©el : ${cpu}%
üìà Load r√©el : ${load}
üïê ${new Date().toLocaleString()}
Host : Local Monitoring (Docker)
Ce message est envoy√© pour v√©rifier que Telegram fonctionne correctement !`;

return [{
  json: {
    alert,                   // true ‚Üí passe dans la branche alerte
    cpu_usage: `${cpu}%`,
    system_load: load,
    message,
    timestamp: new Date().toLocaleString(),
    test_mode: true
  }
}];

